import React from "react";

const CartContext = React.createContext({
  items: [],
  totalAmount: 0,

  // now below we will have two functions that will help us update the context
  addItem: (item) => {},
  // the (item) wirtten above recives the item that should be added, we have used this item term earlier
  removeItem: (id )= {},
  // the remove item recives an id which intructs to remove items frm the cart
});
export default CartContext;
