import { useRef, useState } from "react"; // STEP-3  //
import Input from "../../UI/Input";
import classes from "./MealItemForm.module.css";
// WELCOME DEAR ADDITEMCARTHANDLER from cartprovider
const MealItemForm = (props) => {
  const [amountIsValid, setAmountIsValid]=useState(true)
  const amountInputRef = useRef();
  // STEP - 1
  const submitHandler = (event) => {
    event.preventDefault();

    const enteredAmount = amountInputRef.current.value;
    // its always current property for refs and the value is always a string

    const enteredAmountNumber = +enteredAmount;
    // we are changing this into a number

    if (
      enteredAmount.trim().length === 0 ||
      enteredAmountNumber < 1 ||
      enteredAmountNumber > 5
    ) {
      setAmountIsValid(false)
      return;
      // we also want to set a message to we use useState
    }

      props.onAddToCart(enteredAmountNumber);   // go to meal item to use enteredamountnumber 
  };

  // IMPRTANT NOTE- REFS ARE USED TO READ VALUES WE HAD LEARNT THE EXAPMLE EARLIER
  // STEP -2 to extract the enterd amount we use ref but since Input is
  // a custom component we need to intrust the parent componnet so lets go ther
  return (
    <form className={classes.form} onSubmit={submitHandler}>
      <Input
        ref={amountInputRef} // STEP -4
        label="Amount"
        input={{
          id: "amount_" + props.id,
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
        }}
      />
      <button>+ Add</button>
      {!amountIsValid && <p>Please enter a valid amount (1-5)</p>}
    </form>
  );
};

export default MealItemForm;
