import React, { useContext } from "react";
import classes from "./Cart.module.css";
import Modal from "../UI/Modal";
import CartContext from "../../Store/cart-context";
import CartItem from "./CartItem";

const Cart = (props) => {
  const cartCtx = useContext(CartContext); // step 1

  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`; // step 4

  const hasItems = cartCtx.items.length > 0; // step 5


  const cartItemRemoveHandler = id => {
    cartCtx.removeItem(id);

  }
  const cartItemAddHandler = item =>{
    cartCtx.addItem({...item,amount:1})

  }

  const cartItems = (
    <ul className={classes["cart-items"]}>
      {
        // [{ id: "c1", name: "Sushi", price: 12.99 }]
        cartCtx.items.map((item) => (
          <CartItem 
          key = {item.id}
          name={item.name}
          amount = {item.amount}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}   // we are doing this so that botht the functions recives id and item
          onAdd={cartItemAddHandler.bind(null, item)}
          />
        ))
      }
    </ul>
  );

  return (
    <Modal onHideCart={props.onHideCart}>
      {cartItems}
      <div className={classes.total}>
        {/* step 3 */}
        <span>total Amount</span>
        <span>{totalAmount}</span>
      </div>
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onHideCart}>
          Close
        </button>
        {hasItems && <button className={classes.button}>Order</button>}
      </div>
    </Modal>
  );
};

export default Cart;

