// WE WILL LEARN THE USE OF USEREDUCER
import { useReducer } from "react";

import CartContext from "./cart-context";

// STEP 3-
const defaultCartState = {
  items: [],
  totalAmount: 0,
};
// STEP-1 we add cart reducer outside as this will not need anything from the component
const cartReducer = (state, action) => {
  // STEP-2 in reducer functions we recive a state and action object the action is despatched by you
  // and state is simply the last state managed by the reducer function

  // STEP 8 here we are adding logic to our additemtocarthandler function
  if (action.type === "ADD") {
    const updatedTotalAmount =
      state.totalAmount + action.item.price * action.item.amount;

    const existingCartItemIndex = state.items.findIndex(
      (item) => item.id === action.item.id
    );

    const existingCartItem = state.items[existingCartItemIndex];

    let updatedItems;

    if (existingCartItem) {
      const updatedItem = {
        ...existingCartItem,
        amount: existingCartItem.amount + action.item.amount,
      };
      updatedItems = [...state.items];
      updatedItems[existingCartItemIndex] = updatedItem;
    } else {
      updatedItems = state.items.concat(action.item);
    }


    // now we have to mange the itmes added to per meals basis and also update the total price
    // const updatedItems = state.items.concat(action.item);
    // concat is a built in method in javascript which adds an new item to an array
    // but unlike push it does not edit he existing array but return a new array

    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }

      if(action.type==='REMOVE'){

    const existingCartItemIndex = state.items.findIndex(
      (item) => item.id === action.item.id
    );

    const existingItem = state.items[existingCartItemIndex];

    const updatedTotalAmount = state.totalAmount - existingItem.price
      }
  return defaultCartState; // step 4
};

const CartProvider = (props) => {
  // step 5 , use reducer returns an array with exactly two elements
  // so we are using array destructung to pull out the values from that array and store them in seperate constants
  // the first array elements is always your state snapshot
  // second element is a function that allows you to dispatch the action to the reducer
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );

  // WHENEVER THE BELOW TWO FUNCTIONS ARE CALLED ITEMS AND ID ARE PASSES RESPECTIVELY
  const addItemToCartHandler = (item) => {
    dispatchCartAction({ type: "ADD", item: item });
    //STEP 7 - It's totally upto us what an action is this type and ADD is any arbitary name
    // the second name item is also upto as but we need to point it as like props we are passing item to this function
  };

  const removeItemFromCartHandler = (id) => {
    dispatchCartAction({ type: "REMOVE", id: id });
  };

  const cartContext = {
    items: cartState.items, // WE HAVe defined these values in the defaultCartState
    totalAmount: cartState.totalAmount,
    addItem: addItemToCartHandler, // now we have to use this to add items but for this we have to go to mealsitemform section
    removeItem: removeItemFromCartHandler,
    // STEP-6 INSTEAD OF HARDCODIND WE WILL ACCESS CARTSTATE ITEMS UPAR TOH DEKHO
    // items: 0,
    // totalAmount:0,
    // addItem: addItemToCartHandler,
    // removeItem: removeItemFromCartHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;

