import {useContext} from 'react'

import CartIcon from '../Cart/CartIcon';
import CartContext from '../../Store/cart-context';
import classes from './HeaderCartButton.module.css';

const HeaderCartButton = (props) => {
  const cartCtx =  useContext(CartContext)

  const numberOfCartItems = cartCtx.items.reduce((curNumber, item)=>{   
    // suppose if a user adds 3 sushi in his cart so cart should still display only one item in it
    // for that we use reduce it is a built in that allows us to tranform an array of data into a single value, into a single number in this case
    return curNumber + item.amount;
  },0);

  const btnClasses =`${classes.button} ${classes.bump}`

  return (
    <button className={classes.button} onClick={props.onClick}>
      <span className={classes.icon}>
        <CartIcon />
      </span>
      <span>Your Cart</span>
      <span className={classes.badge}>{numberOfCartItems}</span>
    </button>
  );
};

export default HeaderCartButton;
